import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import ResultChart from '../components/ResultChart';
import axios from 'axios';

const ResultPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const data = location.state;
  const [backtestResult, setBacktestResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!data) {
      navigate('/');
      return;
    }
    const runBacktest = async () => {
      setLoading(true);
      setError('');
      try {
        const response = await axios.post('http://127.0.0.1:8000/run-backtest', {
          code: data.code,
          capital: parseFloat(data.capital),
          capital_pct: parseFloat(data.capitalPct),
          stopLoss: parseFloat(data.stopLoss),
          takeProfit: parseFloat(data.takeProfit),
          startDate: data.startDate,
          endDate: data.endDate,
          commission: parseFloat(data.commission),
        });
        setBacktestResult(response.data);
      } catch (err) {
        setError('백테스트 실행 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };
    runBacktest();
    // eslint-disable-next-line
  }, [data]);

  // 샘플 차트 데이터 (실제 백테스트 연동 전까지)
  const sampleData = {
    labels: ['1월', '2월', '3월', '4월', '5월'],
    values: [10000, 10500, 10200, 10800, 11000],
  };

  return (
    <div>
      <h1>입력한 전략 정보</h1>
      <ul>
        <li><b>전략:</b> {data?.strategy}</li>
        <li><b>자본금:</b> {data?.capital}</li>
        <li><b>투입비율:</b> {data?.capitalPct}</li>
        <li><b>Stop Loss:</b> {data?.stopLoss}%</li>
        <li><b>Take Profit:</b> {data?.takeProfit}%</li>
        <li><b>백테스트 기간:</b> {data?.startDate} ~ {data?.endDate}</li>
        <li><b>수수료율:</b> {data?.commission}</li>
      </ul>
      <h2>변환된 파이썬 코드</h2>
      <pre style={{background: '#222', color: '#fff', padding: '1rem', borderRadius: '8px', overflowX: 'auto'}}>
        {data?.code}
      </pre>
      <h2>백테스트 결과</h2>
      {loading && <div>백테스트 실행 중...</div>}
      {error && <div style={{color: 'red'}}>{error}</div>}
      {backtestResult && (
        <div>
          <b>최종 자본:</b> {backtestResult.final_balance}
        </div>
      )}
      {/* 기존 샘플 차트는 주석 처리 또는 실제 결과로 대체 예정 */}
      {/* <ResultChart data={sampleData} /> */}
    </div>
  );
};

export default ResultPage;
